WITH (
  written_bytes_per_device = (
      smartctl_device{serial_number="$nvme_serial",instance=~"$instance"}
        * on(device,instance) group_right(model_name,serial_number)
        smartctl_device_bytes_written{instance=~"$instance"}
  ) > 0,
  written_bytes_per_serial = max(written_bytes_per_device) without(device),
  written_gigabytes = ((written_bytes_per_serial / 1024) / 1024) / 1024,
  increase_day = increase(written_gigabytes[1d:30m]), # In big steps, smart is not that fast to update.
)
avg_over_time( increase_day[$__range:30m] ) # yearly average of gigabytes/day written

